import { Component } from '@angular/core';
import { SleepService } from '../services/sleep.service';
import { SleepData } from '../data/sleep-data';
import { OvernightSleepData } from '../data/overnight-sleep-data';
import { StanfordSleepinessData } from '../data/stanford-sleepiness-data';

@Component({
  selector: 'app-overnight',
  templateUrl: 'overnight.page.html',
  styleUrls: ['overnight.page.scss'],
})
export class OvernightPage {
	submitted: boolean = false;
	bedTime: string = '';
	wakeupTime: string = '';


  constructor(public sleepService:SleepService) { }

	ngOnInit() {
		console.log(this.allSleepData);
    // set default wakeup time to current time
		var d: Date = new Date();
		this.wakeupTime = d.toLocaleString("en-US", {timeZone: "America/Los_Angeles"});

		// set default bed time to 8 hours ago
		// d.setHours(d.getHours() - 8);
		this.bedTime = d.toLocaleString("en-US", {timeZone: "America/Los_Angeles"});

		// check if there is entry in sleepservice
		this.submitted = false;
	}

	/* Ionic doesn't allow bindings to static variables, so this getter can be used instead. */
	get allSleepData() {
		return SleepService.AllSleepData;
	}

	get allOvernightData() {
		return SleepService.AllOvernightData;
	}

	onSubmit() {
		console.log(this.bedTime);
		console.log(this.wakeupTime);
		console.log(typeof this.bedTime);
		this.sleepService.logOvernightData(new OvernightSleepData(new Date(this.bedTime), new Date(this.wakeupTime)));

		this.submitted = true;
	}

}
