import { Component } from '@angular/core';
import { SleepService } from '../services/sleep.service';
import { SleepData } from '../data/sleep-data';
import { OvernightSleepData } from '../data/overnight-sleep-data';
import { StanfordSleepinessData } from '../data/stanford-sleepiness-data';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
	bedTime: string;
	wakeupTime: string;
	sliderValue: number;
	sliderDescription: string | undefined;

  constructor(public sleepService:SleepService) {
		// set default wakeup time to current time
		var d: Date = new Date();
		this.wakeupTime = d.toLocaleString("en-US", {timeZone: "America/Los_Angeles"});

		// set default bed time to 8 hours ago
		// d.setHours(d.getHours() - 8);
		this.bedTime = d.toLocaleString("en-US", {timeZone: "America/Los_Angeles"});

		this.sliderValue = 1;	// default value
		this.sliderDescription = StanfordSleepinessData.ScaleValues[this.sliderValue];
	}

	ngOnInit() {
		console.log(this.allSleepData);
	}

	/* Ionic doesn't allow bindings to static variables, so this getter can be used instead. */
	get allSleepData() {
		return SleepService.AllSleepData;
	}

	get allSleepinessData() {
		return SleepService.AllSleepinessData;
	}

	onSubmit() {
		console.log(this.bedTime);
		console.log(this.wakeupTime);
		console.log(typeof this.bedTime);
		this.sleepService.logOvernightData(new OvernightSleepData(new Date(this.bedTime), new Date(this.wakeupTime)));
	}

	onSliderChange() {
		console.log(this.sliderValue);
		this.sliderDescription = StanfordSleepinessData.ScaleValues[this.sliderValue];
	}

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Sleep Tracker 
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Sleep Tracker</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-item>
    <ion-title class="ion-text-center">
      <h1 >Good morning</h1>
    </ion-title>
  </ion-item>


  <ion-row class="ion-justify-content-center">
    <ion-col class="time-column">
      <ion-icon name="moon" size="large" class="ion-padding-vertical"></ion-icon>
      <ion-label class="ion-text-center"><h3>What time did you sleep?</h3></ion-label>
      <ion-datetime presentation="time" displayFormat="h:mm" locale="en-US" [(ngModel)]="bedTime"></ion-datetime>
    </ion-col>

    <ion-col class="time-column">
      <ion-icon name="sunny" size="large"></ion-icon>
      <ion-label class="ion-text-center"><h3>What time did you wake up?</h3></ion-label>
      <ion-datetime presentation="time" displayFormat="h:mm" locale="en-US" [(ngModel)]="wakeupTime"></ion-datetime>
    </ion-col>
  </ion-row>


  <ion-item>
    <ion-row class="ion-justify-content-center">
      <ion-col>
        <ion-button (click)="onSubmit()">Submit</ion-button>
      </ion-col>
    </ion-row>
  </ion-item>

  <ion-list>
    <div *ngFor="let bullet of allSleepinessData">
      <ion-item>l
      </ion-item>
    </div>
  </ion-list>

  <ion-item>
    <ion-title size="large" class="ion-text-center">Record sleepiness</ion-title>
  </ion-item>

  <ion-range min="1" max="7" (ionChange)="onSliderChange()" [(ngModel)]="sliderValue">
    <ion-icon slot="start" name="flash"></ion-icon>
    <ion-icon slot="end" name="cloudy-night"></ion-icon>
  </ion-range>
  <ion-row class="ion-justify-content-between">
    <span>Awake</span>
    <span>Sleepy</span>
  </ion-row>

  <ion-text color="primary" class="ion-text-center">
    <p>{{sliderDescription}}</p>
  </ion-text>


</ion-content>
